import React, { useState } from 'react';
import './index.css';
import { useSelector, useDispatch } from 'react-redux';
import Square from '../square'
import { selectCell } from '../../store/actions/moves';

const selectBoard = (state) => state.board
const selectGame = (state) => state.game

const getRandomCell = (i) => Math.floor(Math.random() * i)

export const Board = () => {
  const [state, setState] = useState(["", "", "", "", "", "", "", "", ""])

  const board = useSelector(selectBoard)
  const game = useSelector(selectGame)
  const dispatch = useDispatch()

  return (
    <div className="BoardContainer">
      <div className="Board">
        <div className="row">
          <Square val={board[0]} chooseSquare={() => { alert(0) }} />
          <Square val={board[1]} chooseSquare={() => { alert(1) }} />
          <Square val={board[2]} chooseSquare={() => { alert(2) }} />
        </div>
        <div className="row"></div>
        <div className="row"></div>
        {/* Board: {JSON.stringify(board)}
        <div onClick={() => dispatch(
          selectCell(
            game.currentPlayer,
            getRandomCell(board.length),
            getRandomCell(board.length)
          )
        )}>

        </div> */}
      </div>
    </div>
  )
}
